import React, { useState } from 'react';
import {
  View,
  Text,
  TextInput,
  TouchableOpacity,
  StyleSheet,
  KeyboardAvoidingView,
  Platform,
  Modal,
  ScrollView,
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { LinearGradient } from 'expo-linear-gradient';
import { MaterialIcons as Icon } from '@expo/vector-icons';
import { useAuth } from '../context/AuthContext';
import Toast from 'react-native-toast-message';
import { authAPI } from '../config/api';
import AsyncStorage from '@react-native-async-storage/async-storage';

export default function LoginScreen() {
  const navigation = useNavigation();
  const { setUser, setIsAuthenticated } = useAuth();
  const [phoneNumber, setPhoneNumber] = useState('');
  const [loading, setLoading] = useState(false);
  const [showOtpModal, setShowOtpModal] = useState(false);
  const [otpCode, setOtpCode] = useState('');
  const [otpVerified, setOtpVerified] = useState(false);
  const [isAdminLogin, setIsAdminLogin] = useState(false);

  const handleSendOTP = async (adminMode = false) => {
    setIsAdminLogin(adminMode);

    // Validate phone number - accept any 10-digit number (removes spaces/dashes)
    const cleanedPhone = phoneNumber.replace(/\D/g, ''); // Remove all non-digits
    if (!cleanedPhone || cleanedPhone.length !== 10) {
      Toast.show({
        type: 'error',
        text1: 'Invalid Phone Number',
        text2: 'Please enter a valid 10-digit phone number',
      });
      return;
    }

    // Use cleaned phone number for API calls
    const finalPhoneNumber = cleanedPhone;

    setLoading(true);
    setOtpCode(''); // Clear previous OTP
    try {
      let response;

      if (adminMode) {
        // Direct admin login
        console.log('Admin login mode - sending admin OTP...');
        response = await authAPI.sendAdminLoginOTP({ phone: finalPhoneNumber });
      } else {
        // Try member login first, if fails with 404, try admin login
        try {
          response = await authAPI.sendLoginOTP({ phone: finalPhoneNumber });
        } catch (memberError) {
          // If member login fails with 404, try admin login
          if (memberError.response?.status === 404) {
            console.log('Member not found, trying admin login...');
            response = await authAPI.sendAdminLoginOTP({ phone: finalPhoneNumber });
            setIsAdminLogin(true);
          } else {
            throw memberError; // Re-throw if it's not a 404
          }
        }
      }

      if (response.data.success) {
        setShowOtpModal(true);
        setOtpVerified(false);
        // Show OTP to user (for testing - remove in production)
        if (response.data.otp) {
          Toast.show({
            type: 'info',
            text1: 'OTP Generated',
            text2: `Enter OTP: ${response.data.otp}`,
            visibilityTime: 10000,
          });
          console.log('üîë OTP for login:', response.data.otp);
        }
        Toast.show({
          type: 'success',
          text1: 'OTP Generated',
          text2: 'Please enter the OTP',
        });
      }
    } catch (error) {
      console.error('Send OTP error:', error);
      const errorMsg = error.response?.data?.message || error.response?.data?.error || error.message || 'Failed to send OTP';
      const errorStatus = error.response?.status;

      // If phone doesn't exist, show specific message
      if (errorStatus === 404 || errorMsg.includes('not found') || errorMsg.includes('not registered') || errorMsg.includes('does not exist')) {
        Toast.show({
          type: 'error',
          text1: adminMode ? 'Admin Not Found' : 'Phone Not Registered',
          text2: adminMode 
            ? 'This phone number is not registered as an admin. Please contact support or use member login.'
            : 'This phone number is not registered. Please sign up first.',
          visibilityTime: 5000,
        });
        setShowOtpModal(false);
        setIsAdminLogin(false); // Reset admin mode if not found
      } else {
        Toast.show({
          type: 'error',
          text1: adminMode ? 'Admin OTP Error' : 'Error',
          text2: errorMsg,
          visibilityTime: 5000,
        });
      }
    } finally {
      setLoading(false);
    }
  };

  // Manual OTP verification for login
  const handleVerifyOTP = async () => {
    if (!otpCode || otpCode.length < 4) {
      Toast.show({
        type: 'error',
        text1: 'Invalid OTP',
        text2: 'Please enter a valid OTP code (4-6 digits)',
      });
      return;
    }

    setLoading(true);
    try {
      // Clean phone number for verification
      const cleanedPhoneForVerify = phoneNumber.replace(/\D/g, '');
      console.log('Verifying OTP for login:', { phone: cleanedPhoneForVerify, otp: otpCode });

      let verifyResponse;

      if (isAdminLogin) {
        // Direct admin verification
        console.log('Admin login mode - verifying admin OTP...');
        verifyResponse = await authAPI.verifyAdminLoginOTP({
          phone: cleanedPhoneForVerify,
          otp: otpCode.trim()
        });
      } else {
        // Try member login first, if fails with 404 or purpose mismatch, try admin login
        try {
          verifyResponse = await authAPI.verifyLoginOTP({ 
            phone: cleanedPhoneForVerify,
            otp: otpCode.trim()
          });
        } catch (memberError) {
          // If member login fails (e.g., phone not found, OTP purpose mismatch, or OTP not found), try admin verification
          const errorStatus = memberError.response?.status;
          const errorMsg = memberError.response?.data?.error || memberError.response?.data?.message || '';
          const isPurposeMismatch = errorMsg.includes('purpose mismatch') || errorMsg.includes('Invalid OTP');
          const isNotFound = errorStatus === 404 || errorMsg.includes('not found') || errorMsg.includes('OTP not found');

          if (errorStatus === 404 || isPurposeMismatch || isNotFound) {
            console.log('Member login OTP verification failed, trying admin login OTP verification...', { errorStatus, errorMsg });
            try {
              verifyResponse = await authAPI.verifyAdminLoginOTP({
                phone: cleanedPhoneForVerify,
                otp: otpCode.trim()
              });
              setIsAdminLogin(true);
            } catch (adminError) {
              // If admin verification also fails, throw the admin error
              throw adminError;
            }
          } else {
            throw memberError; // Re-throw other member verification errors
          }
        }
      }

      console.log('Login verify response:', verifyResponse.data);

      if (verifyResponse.data.success) {
        setOtpVerified(true);
        setShowOtpModal(false);

        // Store token and user data
        const { token, user } = verifyResponse.data;
        await AsyncStorage.setItem('authToken', token);
        await AsyncStorage.setItem('userData', JSON.stringify(user));

        // Update auth context
        setUser(user);
        setIsAuthenticated(true);

        Toast.show({
          type: 'success',
          text1: 'Success',
          text2: 'Login successful!',
        });

        // Navigate to member dashboard - use reset to clear navigation stack
        setTimeout(() => {
          navigation.reset({
            index: 0,
            routes: [{ name: 'MemberTabs' }],
          });
        }, 500);
      } else {
        Toast.show({
          type: 'error',
          text1: 'Invalid OTP',
          text2: verifyResponse.data.message || 'Please enter the correct OTP',
        });
      }
    } catch (error) {
      console.error('OTP verification error:', error);
      const errorMsg = error.response?.data?.message || error.response?.data?.error || error.message || 'Please try again';

      if (errorMsg.includes('not found') || errorMsg.includes('expired')) {
        Toast.show({
          type: 'error',
          text1: 'OTP Expired',
          text2: 'Please request a new OTP',
        });
        // Auto-resend OTP
        setTimeout(() => {
          handleSendOTP(isAdminLogin);
        }, 1000);
      } else {
        Toast.show({
          type: 'error',
          text1: 'Verification Failed',
          text2: errorMsg,
        });
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={styles.container}
    >
      <ScrollView showsVerticalScrollIndicator={false}>
        <LinearGradient
          colors={['#D4AF37', '#B8941F']}
          style={styles.header}
        >
          <TouchableOpacity
            style={styles.backButton}
            onPress={() => navigation.goBack()}
          >
            <Icon name="arrow-back" size={24} color="#fff" />
          </TouchableOpacity>
          <Text style={styles.headerIcon}>üè¶</Text>
          <Text style={styles.headerTitle}>Gold Investment</Text>
          <Text style={styles.headerSubtitle}>Secure your financial future</Text>
        </LinearGradient>

        <View style={styles.formContainer}>
          <Text style={styles.formTitle}>Welcome Back</Text>
          <Text style={styles.formSubtitle}>Sign in to continue</Text>

          <View style={styles.inputGroup}>
            <Icon name="phone" size={20} color="#666" style={styles.inputIcon} />
            <TextInput
              style={styles.input}
              placeholder="Phone Number"
              value={phoneNumber}
              onChangeText={(text) => setPhoneNumber(text.replace(/[^0-9]/g, ''))}
              keyboardType="phone-pad"
              maxLength={10}
              autoFocus
            />
          </View>

          <TouchableOpacity
            style={[styles.button, (loading || phoneNumber.length !== 10) && styles.buttonDisabled]}
            onPress={() => handleSendOTP(false)}
            disabled={loading || phoneNumber.length !== 10}
          >
            <Text style={styles.buttonText}>
              {loading ? 'Sending OTP...' : 'Send OTP'}
            </Text>
          </TouchableOpacity>

          {/* Admin Login Button - More Prominent */}
          <View style={styles.adminSection}>
            <View style={styles.divider}>
              <View style={styles.dividerLine} />
              <Text style={styles.dividerText}>OR</Text>
              <View style={styles.dividerLine} />
            </View>

            <TouchableOpacity
              style={[styles.adminButton, (loading || phoneNumber.length !== 10) && styles.buttonDisabled]}
              onPress={() => handleSendOTP(true)}
              disabled={loading || phoneNumber.length !== 10}
            >
              <Icon name="admin-panel-settings" size={22} color="#fff" style={{ marginRight: 10 }} />
              <Text style={styles.adminButtonText}>
                {loading ? 'Sending Admin OTP...' : 'Login as Admin'}
              </Text>
            </TouchableOpacity>

            <Text style={styles.adminHint}>
              Use this button if you're an administrator
            </Text>
          </View>

          <View style={styles.divider}>
            <View style={styles.dividerLine} />
            <Text style={styles.dividerText}>OR</Text>
            <View style={styles.dividerLine} />
          </View>

          <TouchableOpacity
            style={styles.linkButton}
            onPress={() => navigation.navigate('Signup')}
          >
            <Text style={styles.linkText}>
              Don't have an account? <Text style={styles.linkTextBold}>Sign Up</Text>
            </Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.linkButton}
            onPress={() => navigation.navigate('AgentSignup')}
          >
            <Text style={styles.linkText}>
              Want to become an agent? <Text style={styles.linkTextBold}>Agent Signup</Text>
            </Text>
          </TouchableOpacity>

          <TouchableOpacity
            style={styles.linkButton}
            onPress={() => navigation.navigate('AdminSignup')}
          >
            <Text style={styles.linkText}>
              Admin? <Text style={styles.linkTextBold}>Admin Signup</Text>
            </Text>
          </TouchableOpacity>
        </View>
      </ScrollView>

      {/* OTP Verification Modal */}
      <Modal
        visible={showOtpModal && !otpVerified}
        animationType="slide"
        transparent={true}
        onRequestClose={() => {
          if (!loading) {
            setShowOtpModal(false);
          }
        }}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <TouchableOpacity
              style={styles.closeButton}
              onPress={() => {
                if (!loading) {
                  setShowOtpModal(false);
                }
              }}
              disabled={loading}
            >
              <Icon name="close" size={24} color="#333" />
            </TouchableOpacity>

            <Text style={styles.modalTitle}>Verify OTP</Text>
            <Text style={styles.modalSubtitle}>
              Enter the OTP sent to {phoneNumber.replace(/\D/g, '')}
            </Text>

            {/* Manual OTP Input */}
            <View style={styles.manualOtpContainer}>
              <View style={styles.inputGroup}>
                <Icon name="lock" size={20} color="#666" style={styles.inputIcon} />
                <TextInput
                  style={styles.input}
                  placeholder="Enter 6-digit OTP"
                  value={otpCode}
                  onChangeText={(text) => setOtpCode(text.replace(/[^0-9]/g, ''))}
                  keyboardType="number-pad"
                  maxLength={6}
                  autoFocus
                  placeholderTextColor="#999"
                />
              </View>

              {/* Show OTP hint in development mode */}
              {__DEV__ && (
                <Text style={styles.otpHint}>
                  üí° Check backend terminal/console for OTP code
                </Text>
              )}

              <TouchableOpacity
                style={[styles.button, (loading || otpCode.length < 4) && styles.buttonDisabled]}
                onPress={handleVerifyOTP}
                disabled={loading || otpCode.length < 4}
              >
                <Text style={styles.buttonText}>
                  {loading ? 'Verifying...' : 'Verify OTP & Login'}
                </Text>
              </TouchableOpacity>

              <TouchableOpacity
                style={styles.resendButton}
                onPress={() => handleSendOTP(isAdminLogin)}
                disabled={loading}
              >
                <Text style={styles.resendText}>
                  Didn't receive? <Text style={styles.resendTextBold}>Resend OTP</Text>
                </Text>
              </TouchableOpacity>
            </View>
          </View>
        </View>
      </Modal>
    </KeyboardAvoidingView>
  );
}

